class Solution {
    
    
    public int numberOfSubstrings(String s) {
        int ans = 0;
        List<Integer> a = new ArrayList<>();
        List<Integer> b = new ArrayList<>();
        List<Integer> c = new ArrayList<>();
        for(int i = 0;i<s.length();i++){
            char cc = s.charAt(i);
            if(cc=='a')  a.add(i);
            if(cc=='b')  b.add(i);
            if(cc=='c')  c.add(i);
        }
        int n = s.length()-1;
        while(a.size()>0 && b.size()>0 && c.size()>0){
        
            int mn = Math.min((int)a.get(0),Math.min((int)b.get(0),c.get(0)));
            int mx = Math.max((int)a.get(0),Math.max((int)b.get(0),c.get(0)));
            
            ans+=(1+n-mx);
            if((int)a.get(0) == mn) a.remove(0);
            if((int)b.get(0) == mn) b.remove(0);
            if((int)c.get(0) == mn) c.remove(0);
        
        }
        
        return ans;
    }
}
